// This script is needed for anonymously logging search terms by the Squids; see [[wikt:de:MediaWiki:If-search.js]]
// The search URL will contain the search term
// Please modify retroactively

function tfSearch(s,t) {
 if (document.getElementById(s)) {
  document.getElementById(s).setAttribute("onsubmit", "SubSearch("+t+")");
  document.getElementById(s).parentNode.innerHTML = document.getElementById(s).parentNode.innerHTML; // f***ing IE
 }
}
function LogSearch() {
 tfSearch('searchform',1);
 tfSearch('search',3);
 tfSearch('powersearch',2);
 tfSearch('bodySearch',4);
 oSEAp = document.getElementById("searchform").action.replace(/^.*\/wiki\//, "");
}

jQuery(document).ready(LogSearch);

function SubSearch(s) {
 var x="/";
 if (s<2) {
  if (document.getElementById("searchInput").value == "") {x="";}
  document.getElementById("searchform").action = "/wiki/"+oSEAp+x+document.getElementById("searchInput").value;
 } else if (s<3) {
  if (document.getElementById("powerSearchText").value == "") {x="";}
  document.getElementById("powersearch").getElementsByTagName("input")[0].value = oSEAp+x+document.getElementById("powerSearchText").value;
 } else if (s<4) {
  if (document.getElementById("searchText").value == "") {x="";}
  document.getElementById("search").firstChild.value = oSEAp+x+document.getElementById("searchText").value;
 } else if (s<5) {
  if (document.getElementById("bodySearchIput").value == "") {x="";}
  document.getElementById("bodySearch").action = "/wiki/"+oSEAp+x+document.getElementById("bodySearchIput").value;
 }
}

